package tutorial1;

public class Variables_and_data_types {

    public static void main(String[] args) {
        // TODO Auto-generated method stub
        // primitive, not changeable
        int hello_world = 9; // this is an integer, it is a whole number
        double num2 = 5.0; // this is a double, which is a number with a decimal point
        boolean b = true; // this is a boolean, true or false
        char c = 'h'; // this is a char, it is a single character
        // not primitive, changeable
        String str = "9"; // this is a string, it is a sequence of characters

        // String tim = hello_world; // int cannot be converted to string

        System.out.println(hello_world);
    }

}
